import React, { useEffect, useState } from "react";
import axiosInstance from "../axiosInstance";

const AdminHomePageContentPage = () => {
  const [content, setContent] = useState({
    title: "",
    description: "",
    contact_email: "",
    contact_phone: "",
    contact_address: "",
    is_active: true
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    const fetchContent = async () => {
      try {
        setLoading(true);
        const res = await axiosInstance.get("/api/portfolio/homepage-content/");
        setContent(res.data);
      } catch (err) {
        setError("Помилка завантаження: " + (err.response?.data?.detail || err.message));
      } finally {
        setLoading(false);
      }
    };
    fetchContent();
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setContent(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    setError("");
    setSuccess("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setError("");
    setSuccess("");

    if (!content.title || !content.description || !content.contact_email) {
      setError("Заповніть всі обов'язкові поля (заголовок, опис, email)");
      setSubmitting(false);
      return;
    }

    try {
      await axiosInstance.put("/api/portfolio/homepage-content/", content);
      setSuccess("Контент успішно збережено!");
      setTimeout(() => setSuccess(""), 3000);
    } catch (err) {
      setError("Помилка збереження: " + (err.response?.data?.detail || err.message));
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) return <div style={{ textAlign: "center", padding: 40 }}>Завантаження...</div>;

  return (
    <div className="container mt-4">
      <h2>Редагування контенту головної сторінки</h2>
      
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}
      
      {success && (
        <div className="alert alert-success" role="alert">
          {success}
        </div>
      )}

      <form onSubmit={handleSubmit} className="card p-4">
        <h5 className="mb-4">Основний контент</h5>
        
        <div className="mb-3">
          <label className="form-label">Заголовок *</label>
          <input
            type="text"
            name="title"
            className="form-control"
            value={content.title}
            onChange={handleChange}
            required
            placeholder="Наприклад: Ласкаво просимо до нашої фотостудії"
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Опис *</label>
          <textarea
            name="description"
            className="form-control"
            rows="4"
            value={content.description}
            onChange={handleChange}
            required
            placeholder="Опис студії (2-3 речення)"
          />
        </div>

        <div className="mb-3">
          <div className="form-check">
            <input
              className="form-check-input"
              type="checkbox"
              name="is_active"
              id="is_active"
              checked={content.is_active}
              onChange={handleChange}
            />
            <label className="form-check-label" htmlFor="is_active">
              Показувати на головній сторінці
            </label>
          </div>
        </div>

        <hr className="my-4" />
        
        <h5 className="mb-4">Контактна інформація</h5>

        <div className="mb-3">
          <label className="form-label">Email для контактів *</label>
          <input
            type="email"
            name="contact_email"
            className="form-control"
            value={content.contact_email}
            onChange={handleChange}
            required
            placeholder="info@studio.com"
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Телефон</label>
          <input
            type="text"
            name="contact_phone"
            className="form-control"
            value={content.contact_phone || ""}
            onChange={handleChange}
            placeholder="+380 XX XXX XX XX"
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Адреса</label>
          <textarea
            name="contact_address"
            className="form-control"
            rows="2"
            value={content.contact_address || ""}
            onChange={handleChange}
            placeholder="Вулиця, будинок, місто"
          />
        </div>

        <button
          type="submit"
          className="btn btn-primary"
          disabled={submitting}
        >
          {submitting ? "Збереження..." : "Зберегти зміни"}
        </button>
      </form>

      <div className="mt-4 p-3" style={{ background: "#f8f9fa", borderRadius: "8px" }}>
        <h6>Попередній перегляд:</h6>
        <div style={{
          background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
          color: "white",
          padding: "40px 20px",
          textAlign: "center",
          borderRadius: "8px",
          marginTop: "15px"
        }}>
          <h2 style={{ fontSize: "2rem", marginBottom: "15px" }}>
            {content.title || "Заголовок"}
          </h2>
          <p style={{ fontSize: "1.1rem", lineHeight: "1.6" }}>
            {content.description || "Опис"}
          </p>
        </div>
      </div>
    </div>
  );
};

export default AdminHomePageContentPage;
